"use client";

import { Check } from "lucide-react";
import { Badge } from "@/registry/brook/ui/badge/badge";
import { Button } from "@/registry/brook/ui/button/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/registry/brook/ui/card/card";
import data from "../data.json";
import styles from "./pricing-section.module.css";

const { plans } = data;

export default function PricingSection() {
  return (
    <section className={styles.section}>
      <div className={styles.header}>
        <h2 className={styles.title}>Simple, transparent pricing</h2>
        <p className={styles.subtitle}>
          Choose the plan that works best for you. All plans include a 14-day
          free trial.
        </p>
      </div>

      <div className={styles.grid}>
        {plans.map((plan) => (
          <Card
            className={`${styles.card} ${plan.highlighted ? styles.highlighted : ""}`}
            key={plan.name}
          >
            {plan.badge && (
              <Badge className={styles.badge} variant="default">
                {plan.badge}
              </Badge>
            )}
            <CardHeader className={styles.cardHeader}>
              <CardTitle className={styles.planName}>{plan.name}</CardTitle>
              <CardDescription className={styles.planDescription}>
                {plan.description}
              </CardDescription>
            </CardHeader>

            <CardContent className={styles.cardContent}>
              <div className={styles.priceContainer}>
                <span className={styles.currency}>$</span>
                <span className={styles.price}>{plan.monthlyPrice}</span>
                <span className={styles.period}>/month</span>
              </div>

              <ul className={styles.features}>
                {plan.features.map((feature) => (
                  <li className={styles.feature} key={feature}>
                    <div className={styles.checkIcon}>
                      <Check size={10} strokeWidth={3} />
                    </div>
                    <span>{feature}</span>
                  </li>
                ))}
              </ul>
            </CardContent>

            <CardFooter className={styles.cardFooter}>
              <Button
                className={styles.button}
                variant={plan.highlighted ? "primary" : "secondary"}
              >
                {plan.cta}
              </Button>
            </CardFooter>
          </Card>
        ))}
      </div>
    </section>
  );
}

.section {
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
  padding: 4rem 1.5rem;
}

.header {
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  margin-bottom: 3rem;
}

.title {
  font-size: 2.25rem;
  font-weight: 600;
  letter-spacing: -0.025em;
  color: var(--foreground);
  margin: 0;
  line-height: 1.2;
}

.subtitle {
  font-size: 1rem;
  color: var(--muted-foreground);
  margin: 0;
  max-width: 400px;
  line-height: 1.5;
}

.grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 0;
  align-items: stretch;
  padding: 3rem 0;
}

.card {
  position: relative;
  z-index: 1;
  display: flex;
  flex-direction: column;
}

.card:first-child {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  clip-path: inset(-50px 0 -50px -50px);
}

.card:last-child {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  clip-path: inset(-50px -50px -50px 0);
}

.highlighted {
  background-color: oklch(from var(--card) l c h / 0.6);
  z-index: 10;
  margin-top: -1.5rem;
  margin-bottom: -1.5rem;
  padding-top: 3rem;
  padding-bottom: 3rem;
}

.badge {
  position: absolute;
  top: -10px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 0.75rem;
  white-space: nowrap;
}

.cardHeader {
  padding-bottom: 0;
}

.planName {
  font-size: 1.25rem;
  font-weight: 600;
}

.planDescription {
  font-size: 0.875rem;
  line-height: 1.5;
}

.cardContent {
  flex: 1;
}

.priceContainer {
  display: flex;
  align-items: baseline;
  margin-bottom: 2rem;
}

.currency {
  font-size: 1.5rem;
  font-weight: 500;
  color: var(--foreground);
  align-self: flex-start;
  margin-top: 0.5rem;
}

.price {
  font-size: 4rem;
  font-weight: 600;
  letter-spacing: -0.04em;
  color: var(--foreground);
  line-height: 1;
}

.period {
  font-size: 1rem;
  color: var(--muted-foreground);
  margin-left: 0.25rem;
}

.features {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 0.875rem;
}

.feature {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
  color: var(--secondary-foreground);
}

.checkIcon {
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background-color: oklch(from var(--primary) l c h / 0.15);
  color: var(--primary);
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.cardFooter {
  padding-top: 1rem;
}

.button {
  width: 100%;
  transition:
    background-color 0.2s var(--ease-out-expo),
    border-color 0.2s var(--ease-out-expo);
}

@media (max-width: 1024px) {
  .grid {
    grid-template-columns: 1fr;
    max-width: 400px;
    margin: 0 auto;
    gap: 1.5rem;
    padding: 0;
  }

  .card:first-child,
  .card:last-child {
    border-radius: var(--radius);
    clip-path: none;
  }

  .highlighted {
    order: -1;
    margin-top: 0;
    margin-bottom: 0;
    padding-top: 1.5rem;
    padding-bottom: 1.5rem;
  }
}

@media (max-width: 640px) {
  .section {
    padding: 2rem 1rem;
  }

  .title {
    font-size: 1.75rem;
  }

  .subtitle {
    font-size: 0.9375rem;
  }

  .price {
    font-size: 3rem;
  }

  .currency {
    font-size: 1.25rem;
  }

  .feature {
    font-size: 0.875rem;
  }
}
