files.content:
.popup {
  min-width: 280px;
}

.triggerButton {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 6px 16px;
  height: auto;
  min-height: 44px;
  min-width: 180px;
  touch-action: manipulation;
  -webkit-tap-highlight-color: transparent;
}

.triggerButton:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.triggerAvatar {
  width: 32px;
  height: 32px;
  flex-shrink: 0;
}

.triggerText {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 1px;
  min-width: 0;
  flex: 1;
}

.triggerName {
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-weight: 500;
  color: var(--foreground);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  max-width: 100%;
}

.triggerOrg {
  font-size: 0.75rem;
  line-height: 1rem;
  color: var(--muted-foreground);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  max-width: 100%;
}

.profileSection {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  padding: 8px 12px;
  gap: 12px;
}

.profileInfo {
  display: flex;
  align-items: center;
  gap: 12px;
  flex: 1;
  min-width: 0;
}

.avatar {
  width: 40px;
  height: 40px;
  flex-shrink: 0;
}

.profileText {
  display: flex;
  flex-direction: column;
  gap: 2px;
  min-width: 0;
  flex: 1;
}

.profileName {
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-weight: 500;
  color: var(--foreground);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.profileEmail {
  font-size: 0.75rem;
  line-height: 1rem;
  color: var(--muted-foreground);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.freeBadge {
  flex-shrink: 0;
  margin-top: 2px;
}

.submenuTriggerWithIcon {
  display: flex;
  align-items: center;
  gap: 8px;
}

.submenuIcon {
  flex-shrink: 0;
  margin-left: 4px;
  color: var(--muted-foreground);
}

.submenuTriggerWithIcon:hover .submenuIcon {
  color: var(--secondary-foreground);
}

.radioItem {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-right: 12px;
}

.dialogPopup {
  width: 400px;
  padding: 24px;
}

.field {
  margin-top: 16px;
}

.fieldLabel {
  margin-left: 4px;
}

.dialogFooter {
  display: flex;
  gap: 12px;
  margin-top: 24px;
}

.dialogFooter > * {
  flex: 1;
}

.spacer {
  height: 4px;
  width: 100%;
}

.menuItemText {
  margin-left: 4px;
}

@media (max-width: 640px) {
  .popup {
    min-width: 260px;
  }

  .profileSection {
    padding: 6px 10px;
    gap: 8px;
  }

  .profileInfo {
    gap: 10px;
  }

  .avatar {
    width: 36px;
    height: 36px;
  }

  .profileName {
    font-size: 0.8125rem;
  }

  .profileEmail {
    font-size: 0.6875rem;
  }

  .dialogPopup {
    width: 90vw;
    max-width: 400px;
    padding: 20px;
  }

  .dialogFooter {
    margin-top: 20px;
    gap: 8px;
  }
}
