"use client";

import { Progress } from "@base-ui-components/react/progress";
import { cn } from "@/lib/utils";
import styles from "./progress.module.css";

function ProgressRoot({
  className,
  ...props
}: React.ComponentProps<typeof Progress.Root>) {
  return <Progress.Root className={cn(styles.root, className)} {...props} />;
}

function ProgressLabel({
  className,
  ...props
}: React.ComponentProps<typeof Progress.Label>) {
  return <Progress.Label className={cn(styles.label, className)} {...props} />;
}

function ProgressTrack({
  className,
  ...props
}: React.ComponentProps<typeof Progress.Track>) {
  return <Progress.Track className={cn(styles.track, className)} {...props} />;
}

function ProgressIndicator({
  className,
  ...props
}: React.ComponentProps<typeof Progress.Indicator>) {
  return <Progress.Indicator className={cn(styles.indicator, className)} {...props} />;
}

function ProgressValue({
  className,
  ...props
}: React.ComponentProps<typeof Progress.Value>) {
  return <Progress.Value className={cn(styles.value, className)} {...props} />;
}

export {
  ProgressRoot as Progress,
  ProgressLabel,
  ProgressTrack,
  ProgressIndicator,
  ProgressValue,
};

.root {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;
}

.label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
}

.value {
  font-size: 0.75rem;
  color: var(--muted-foreground);
  font-weight: 500;
}

.track {
  position: relative;
  height: 0.375rem;
  width: 100%;
  background-color: var(--mix-card-75-bg);
  border-radius: 9999px;
  overflow: hidden;
}

.indicator {
  height: 100%;
  background-color: var(--primary);
  border-radius: 9999px;
  transition: all 150ms ease-out;
  width: var(--progress-indicator-width);
}

.root[data-indeterminate] .indicator {
  width: 40%;
  animation: indeterminate 2s infinite linear;
}

@keyframes indeterminate {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(250%);
  }
}
