"use client";

interface PayloadEntry {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
}

export interface ChartTooltipProps {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
}

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!active || !payload || !payload.length) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && <div style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}>{formatLabel(label)}</div>}
      {payload.map((entry, index) => (
        <div
          key={index}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>{formatName(entry.name || entry.dataKey || "")}</span>
          </div>
          <span
            style={{
              fontFamily: "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import {
  RadarChart as RechartsRadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  ResponsiveContainer,
  Tooltip,
} from "recharts";
import ChartTooltip from "./chart-tooltip";
import styles from "./radar-chart.module.css";

export interface RadarChartData {
  category: string;
  value: number;
  type: string;
}

export interface RadarChartProps {
  data: RadarChartData[];
  levels?: number;
  maxValue?: number;
  showLabels?: boolean;
  showGrid?: boolean;
  showRadialAxis?: boolean;
  fillOpacity?: number;
  animated?: boolean;
}

function RadarChart({
  data,
  levels = 5,
  maxValue,
  showLabels = true,
  showGrid = true,
  showRadialAxis = true,
  fillOpacity = 0.3,
  animated = false,
}: RadarChartProps) {
  const colors = ["var(--chart1)", "var(--chart2)"];

  const uniqueTypes = Array.from(new Set(data.map((d) => d.type)));

  interface GroupedDataItem {
    category: string;
    [key: string]: string | number;
  }

  const groupedData = data.reduce((acc: GroupedDataItem[], curr) => {
    const existing = acc.find((item) => item.category === curr.category);
    if (existing) {
      existing[curr.type] = curr.value;
    } else {
      acc.push({
        category: curr.category,
        [curr.type]: curr.value,
      });
    }
    return acc;
  }, []);

  return (
    <div
      className={styles.radarChart}
      style={{
        position: "relative",
        width: "100%",
        height: "100%",
        backgroundColor: "var(--card)",
        borderRadius: "14px",
      }}
    >
      <ResponsiveContainer width="100%" height="100%">
        <RechartsRadarChart data={groupedData} margin={{ top: 20, right: 80, bottom: 20, left: 80 }}>
          {showGrid && <PolarGrid stroke="var(--border)" strokeOpacity={0.3} />}
          {showLabels && <PolarAngleAxis dataKey="category" tick={{ fontSize: 11, fill: "var(--muted-foreground)" }} />}
          {showRadialAxis && (
            <PolarRadiusAxis
              angle={90}
              domain={[0, maxValue || "dataMax"]}
              tick={{ fontSize: 10, fill: "var(--muted-foreground)" }}
              axisLine={false}
              tickCount={levels + 1}
            />
          )}
          <Tooltip cursor={{ stroke: "var(--secondary)", strokeWidth: 1 }} content={<ChartTooltip />} />
          {uniqueTypes.map((type, index) => (
            <Radar
              key={type}
              name={type}
              dataKey={type}
              stroke={colors[index % colors.length]}
              fill={colors[index % colors.length]}
              fillOpacity={fillOpacity}
              strokeWidth={2}
              animationDuration={animated ? 800 : 0}
              animationBegin={animated ? index * 200 : undefined}
            />
          ))}
        </RechartsRadarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default RadarChart;

.radarChart {
    position: relative;
    width: 100%;
    height: 100%;
}

.radarChart *:focus,
.radarChart *:focus-visible {
    outline: none !important;
}
