"use client";

type PayloadEntry = {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
};

export type ChartTooltipProps = {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
};

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!(active && payload && payload.length)) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && (
        <div
          style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}
        >
          {formatLabel(label)}
        </div>
      )}
      {payload.map((entry, index) => (
        <div
          key={index}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>
              {formatName(entry.name || entry.dataKey || "")}
            </span>
          </div>
          <span
            style={{
              fontFamily:
                "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import {
  RadialBar,
  RadialBarChart as RechartsRadialBarChart,
  ResponsiveContainer,
  Tooltip,
} from "recharts";
import ChartTooltip from "./chart-tooltip";
import styles from "./radial-bar-chart.module.css";

export type RadialBarChartData = {
  category: string;
  value: number;
};

export type RadialBarChartProps = {
  data: RadialBarChartData[];
  innerRadius?: number | string;
  outerRadius?: number | string;
  showLabels?: boolean;
  animated?: boolean;
};

function RadialBarChart({
  data,
  innerRadius,
  outerRadius,
  animated = false,
}: RadialBarChartProps) {
  const colors = [
    "var(--chart1)",
    "var(--chart2)",
    "var(--accent)",
    "var(--warning)",
    "var(--destructive)",
  ];

  // Sort data from highest to lowest values (outside to inside)
  const sortedData = [...data].sort((a, b) => b.value - a.value);

  const transformedData = sortedData.map((item, index) => ({
    ...item,
    fill:
      item.category.toLowerCase() === "sleep"
        ? "var(--success)"
        : colors[index % colors.length],
  }));

  const tooltipValueFormatter = (value: number | string, _name?: string) => {
    const numValue =
      typeof value === "number" ? value : Number.parseFloat(String(value));
    return Number.isNaN(numValue) ? String(value) : numValue.toLocaleString();
  };

  type TooltipPayload = {
    value: number;
    payload: RadialBarChartData & { fill: string };
  };

  function CustomTooltip({
    active,
    payload,
  }: {
    active?: boolean;
    payload?: TooltipPayload[];
    label?: string | number;
  }) {
    if (!(active && payload && payload.length)) {
      return null;
    }

    const data = payload[0];
    return (
      <ChartTooltip
        active={active}
        payload={[
          {
            ...data,
            name: data.payload.category,
            color: data.payload.fill,
          },
        ]}
        valueFormatter={tooltipValueFormatter}
      />
    );
  }

  return (
    <div className={styles.radialBarChart}>
      <ResponsiveContainer height="100%" width="100%">
        <RechartsRadialBarChart
          barCategoryGap="2%"
          cx="50%"
          cy="50%"
          data={transformedData}
          innerRadius={innerRadius || "20%"}
          outerRadius={outerRadius || "80%"}
        >
          <RadialBar
            animationBegin={animated ? 0 : undefined}
            animationDuration={animated ? 800 : 0}
            background={{ fill: "var(--mix-card-50-bg)" }}
            cornerRadius={4}
            dataKey="value"
          />
          <Tooltip content={<CustomTooltip />} />
        </RechartsRadialBarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default RadialBarChart;

.radialBarChart {
  position: relative;
  width: 100%;
  height: 100%;
  background-color: transparent;
  border-radius: 14px;
}

.radialBarChart *:focus,
.radialBarChart *:focus-visible {
  outline: none !important;
}

.radialBarChart .recharts-radial-bar-background-sector,
.radialBarChart .recharts-sector[fill="#f3f4f6"] {
  fill: var(--secondary) !important;
  opacity: 1 !important;
}

.radialBarChart g[class*="recharts-radial-bar"] path[fill="#f3f4f6"],
.radialBarChart g[class*="recharts-radial-bar"] path[fill="white"],
.radialBarChart g[class*="recharts-radial-bar"] path[fill="#ffffff"] {
  fill: var(--secondary) !important;
}

.centerLabel {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: var(--foreground);
  font-size: 12px;
}
