"use client";

import { Radio } from "@base-ui-components/react/radio";
import { RadioGroup } from "@base-ui-components/react/radio-group";
import { cn } from "@/lib/utils";
import styles from "./radio.module.css";

function RadioGroupRoot({
  className,
  ...props
}: React.ComponentProps<typeof RadioGroup>) {
  return <RadioGroup className={cn(styles.group, className)} {...props} />;
}

function RadioRoot({
  className,
  ...props
}: React.ComponentProps<typeof Radio.Root>) {
  return <Radio.Root className={cn(styles.root, className)} {...props} />;
}

function RadioIndicator({
  className,
  ...props
}: React.ComponentProps<typeof Radio.Indicator>) {
  return (
    <Radio.Indicator className={cn(styles.indicator, className)} {...props} />
  );
}

export { RadioRoot as Radio, RadioGroupRoot as RadioGroup, RadioIndicator };

.group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.root {
  aspect-ratio: 1;
  height: 1rem;
  width: 1rem;
  border-radius: 50%;
  border: 1px solid var(--border);
  background-color: oklch(from var(--accent) l c h / 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  flex-shrink: 0;
}

.root:hover {
  border-color: oklch(from var(--secondary-foreground) l c h / 0.4);
}

.root[data-focused] {
  outline: none;
  border-color: var(--ring);
  box-shadow: 0 0 0 2px var(--ring);
}

.root[data-checked] {
  border-color: oklch(from var(--secondary-foreground) l c h / 0.6);
}

.root[data-checked]:hover {
  border-color: oklch(from var(--secondary-foreground) l c h / 0.8);
}

.root[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.indicator {
  width: 0.5rem;
  height: 0.5rem;
  border-radius: 50%;
  background-color: var(--primary);
  transform: scale(0);
}

.root[data-checked] .indicator {
  transform: scale(1);
}
