"use client";

import { Radio } from "@base-ui-components/react/radio";
import { RadioGroup } from "@base-ui-components/react/radio-group";
import { cn } from "@/lib/utils";
import styles from "./radio.module.css";

function RadioGroupRoot({ className, ...props }: React.ComponentProps<typeof RadioGroup>) {
  return <RadioGroup className={cn(styles.group, className)} {...props} />;
}

function RadioRoot({ className, ...props }: React.ComponentProps<typeof Radio.Root>) {
  return <Radio.Root className={cn(styles.root, className)} {...props} />;
}

function RadioIndicator({ className, ...props }: React.ComponentProps<typeof Radio.Indicator>) {
  return <Radio.Indicator className={cn(styles.indicator, className)} {...props} />;
}

export { RadioGroupRoot as RadioGroup, RadioRoot as Radio, RadioIndicator };

.group {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.root {
    aspect-ratio: 1;
    height: 1rem;
    width: 1rem;
    border-radius: 50%;
    border: 1px solid var(--border);
    background-color: var(--background);
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 150ms ease-out;
    flex-shrink: 0;
}

.root:hover {
    border-color: var(--ring);
}

.root[data-focused] {
    outline: none;
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
}

.root[data-checked] {
    border-color: var(--primary);
    background-color: var(--primary);
}

.root[data-checked]:hover {
    background-color: var(--primary);
    border-color: var(--primary);
}

.root[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
}

.indicator {
    width: 0.375rem;
    height: 0.375rem;
    border-radius: 50%;
    background-color: var(--primary-foreground);
    transform: scale(0);
    transition: transform 150ms ease-out;
}

.root[data-checked] .indicator {
    transform: scale(1);
}
