"use client";

import { ScrollArea } from "@base-ui-components/react/scroll-area";
import { cn } from "@/lib/utils";
import styles from "./scroll-area.module.css";

function ScrollAreaRoot({
  className,
  ...props
}: ScrollArea.Root.Props) {
  return <ScrollArea.Root className={cn(styles.root, className)} {...props} />;
}

function ScrollAreaViewport({
  className,
  ...props
}: ScrollArea.Viewport.Props) {
  return (
    <ScrollArea.Viewport
      data-slot="scrollarea-viewport"
      className={cn(styles.viewport, className)}
      {...props}
    />
  );
}

function ScrollAreaContent({
  className,
  ...props
}: ScrollArea.Content.Props) {
  return (
    <ScrollArea.Content data-slot="scrollarea-content" className={cn(styles.content, className)} {...props} />
  );
}

function ScrollAreaScrollbar({
  className,
  orientation = "vertical",
  ...props
}: ScrollArea.Scrollbar.Props) {
  return (
    <ScrollArea.Scrollbar
      data-slot="scrollarea-scrollbar"
      className={cn(
        styles.scrollbar,
        orientation === "horizontal" && styles.horizontal,
        className
      )}
      orientation={orientation}
      {...props}
    />
  );
}

function ScrollAreaThumb({
  className,
  ...props
}: ScrollArea.Thumb.Props) {
  return (
    <ScrollArea.Thumb data-slot="scrollarea-thumb" className={cn(styles.thumb, className)} {...props} />
  );
}

function ScrollAreaCorner({
  className,
  ...props
}: ScrollArea.Corner.Props) {
  return (
    <ScrollArea.Corner data-slot="scrollarea-corner" className={cn(styles.corner, className)} {...props} />
  );
}

export {
  ScrollAreaRoot as ScrollArea,
  ScrollAreaContent,
  ScrollAreaCorner,
  ScrollAreaScrollbar,
  ScrollAreaThumb,
  ScrollAreaViewport,
};

.root {
  box-sizing: border-box;
  width: 24rem;
  height: 8.5rem;
  max-width: calc(100vw - 8rem);

  @media (max-width: 640px) {
    width: 100%;
    height: 12rem;
    max-width: 100%;
  }
}

.viewport {
  height: 100%;
  border-radius: 0.375rem;
  outline-offset: -1px;
  overscroll-behavior: contain;
  overflow: scroll;
  scrollbar-width: none;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    display: none;
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
  }
}

.content {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding-block: 0.75rem;
  padding-left: 1rem;
  padding-right: 1.5rem;

  @media (max-width: 640px) {
    padding-left: 1rem;
    padding-right: 1rem;
  }
}

.scrollbar {
  display: flex;
  justify-content: center;
  background-color: var(--border);
  width: 0.25rem;
  border-radius: 0.375rem;
  margin: 0.5rem;
  opacity: 0;
  transition: opacity 150ms 300ms;
  touch-action: none;
  user-select: none;

  &[data-scrolling] {
    opacity: 1;
    transition-duration: 75ms;
    transition-delay: 0ms;
  }

  &[data-hovering]:not([data-scrolling]) {
    opacity: 0;
  }

  &::before {
    content: "";
    position: absolute;
    width: 1.25rem;
    height: 100%;
  }

  @media (max-width: 640px) {
    width: 0.375rem;
    margin: 0.625rem;

    &::before {
      width: 1.5rem;
    }
  }
}

.thumb {
  width: 100%;
  border-radius: inherit;
  background-color: var(--muted-foreground);
}

.corner {
  display: none;
}
