"use client";

import { Select } from "@base-ui-components/react/select";
import { Check, ChevronDown } from "lucide-react";
import { cn } from "@/lib/utils";
import styles from "./select.module.css";

function SelectRoot({ ...props }: React.ComponentProps<typeof Select.Root>) {
  return <Select.Root {...props} />;
}

function SelectTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.Trigger>) {
  return (
    <Select.Trigger
      className={cn(styles.trigger, className)}
      data-slot="select-trigger"
      nativeButton
      {...props}
    >
      {children}
    </Select.Trigger>
  );
}

function SelectValue({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.Value>) {
  return (
    <Select.Value className={cn(styles.value, className)} {...props}>
      {children}
    </Select.Value>
  );
}

function SelectIcon({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.Icon>) {
  return (
    <Select.Icon className={cn(styles.icon, className)} {...props}>
      {children || <ChevronDown size={16} />}
    </Select.Icon>
  );
}

function SelectPortal({
  ...props
}: React.ComponentProps<typeof Select.Portal>) {
  return <Select.Portal {...props} />;
}

function SelectOverlay({
  className,
  ...props
}: React.ComponentProps<typeof Select.Backdrop>) {
  return (
    <Select.Backdrop
      className={cn(styles.backdrop, className)}
      data-slot="select-backdrop"
      {...props}
    />
  );
}

function SelectPositioner({
  className,
  ...props
}: React.ComponentProps<typeof Select.Positioner>) {
  return (
    <Select.Positioner
      className={cn(styles.positioner, className)}
      data-slot="select-positioner"
      {...props}
    />
  );
}

function SelectScrollUpArrow({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.ScrollUpArrow>) {
  return (
    <Select.ScrollUpArrow
      className={cn(styles.scrollArrow, className)}
      data-slot="select-scrolluparrow"
      {...props}
    >
      {children || (
        <ChevronDown size={16} style={{ transform: "rotate(180deg)" }} />
      )}
    </Select.ScrollUpArrow>
  );
}

function SelectPopup({
  className,
  ...props
}: React.ComponentProps<typeof Select.Popup>) {
  return <Select.Popup className={cn(styles.popup, className)} {...props} />;
}

function SelectArrow({
  className,
  ...props
}: React.ComponentProps<typeof Select.Arrow>) {
  return <Select.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function SelectScrollDownArrow({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.ScrollDownArrow>) {
  return (
    <Select.ScrollDownArrow
      className={cn(styles.scrollArrow, className)}
      data-slot="select-scrolldownarrow"
      {...props}
    >
      {children || <ChevronDown size={16} />}
    </Select.ScrollDownArrow>
  );
}

function SelectList({
  className,
  ...props
}: React.ComponentProps<typeof Select.List>) {
  return <Select.List className={cn(styles.list, className)} {...props} />;
}

function SelectItem({
  className,
  ...props
}: React.ComponentProps<typeof Select.Item>) {
  return <Select.Item className={cn(styles.item, className)} {...props} />;
}

function SelectItemText({
  className,
  ...props
}: React.ComponentProps<typeof Select.ItemText>) {
  return (
    <Select.ItemText
      className={cn(styles.itemText, className)}
      data-slot="select-itemtext"
      {...props}
    />
  );
}

function SelectGroup({
  className,
  ...props
}: React.ComponentProps<typeof Select.Group>) {
  return (
    <Select.Group
      className={cn(styles.selectGroup, className)}
      data-slot="select-group"
      {...props}
    />
  );
}

function SelectGroupLabel({
  className,
  ...props
}: React.ComponentProps<typeof Select.GroupLabel>) {
  return (
    <Select.GroupLabel
      className={cn(styles.groupLabel, className)}
      data-slot="select-grouplabel"
      {...props}
    />
  );
}

function SelectSeparator({
  className,
  ...props
}: React.ComponentProps<typeof Select.Separator>) {
  return (
    <Select.Separator
      className={cn(styles.separator, className)}
      data-slot="select-separator"
      {...props}
    />
  );
}

function SelectItemIndicator({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Select.ItemIndicator>) {
  return (
    <Select.ItemIndicator
      className={cn(styles.indicator, className)}
      data-slot="select-itemindicator"
      {...props}
    >
      {children || <Check size={16} />}
    </Select.ItemIndicator>
  );
}

function SelectSpacer() {
  return <div style={{ height: "4px", width: "100%" }} />;
}

export {
  SelectRoot as Select,
  SelectArrow,
  SelectGroup,
  SelectGroupLabel,
  SelectIcon,
  SelectItem,
  SelectItemIndicator,
  SelectItemText,
  SelectList,
  SelectOverlay,
  SelectPopup,
  SelectPortal,
  SelectPositioner,
  SelectRoot,
  SelectScrollDownArrow,
  SelectScrollUpArrow,
  SelectSeparator,
  SelectSpacer,
  SelectTrigger,
  SelectValue,
};

.container {
  position: relative;
  display: inline-block;
  z-index: 150;
}

.trigger {
  min-width: 9rem;
  background-color: var(--mix-card-50-bg);

  &:hover:not(.disabled) {
    background-color: var(--accent);
    color: var(--foreground);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &[data-popup-open] {
    background-color: var(--muted);
    color: var(--foreground);

    & .icon {
      transform: rotate(180deg);
    }
  }

  &.disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }

  @media (max-width: 768px) {
    min-width: 7rem;
  }
}

.value {
  flex: 1;
  text-align: left;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  line-height: 1.25 !important;

  &[data-placeholder] {
    color: var(--muted-foreground);
  }
}

.icon {
  margin-left: 0.5rem;
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  opacity: 0.5;
  transition: transform 0.2s ease-in-out;
}

.positioner {
  z-index: 150;
}

.popup {
  width: var(--anchor-width);
  background: var(--popover);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  transform-origin: var(--transform-origin);
  overflow-y: auto;
  z-index: 150;

  transition:
    transform 150ms,
    opacity 150ms;

  &[data-starting-style] {
    opacity: 0;
    transform: scale(0.9);
  }

  &[data-ending-style] {
    transition: none;
  }

  &[data-side="none"] {
    transition: none;
    transform: none;
    opacity: 1;
  }
}

.arrow {
  width: 0.75rem;
  height: 0.75rem;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  border-right: none;
  border-bottom: none;
  transform: rotate(45deg);
  z-index: -1;
}

.scrollArrow {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1.5rem;
  background: var(--mix-card-33-bg);
  border: none;
  cursor: pointer;
  color: var(--foreground);
  transition: background-color 0.2s ease-in-out;

  &:hover {
    background-color: var(--muted);
  }

  &[data-state="hidden"] {
    display: none;
  }
}

.list {
  padding-block: 0.25rem;
  scroll-padding-block: 1.5rem;
  padding-left: 0.25rem;
  padding-right: 0.25rem;
}

.item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-block: 0.5rem;
  padding-left: 0.75rem;
  padding-right: 1rem;
  font-size: 0.875rem;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3125rem;

  outline: none;

  &:hover:not(.itemDisabled) {
    background-color: var(--accent);
  }

  &[data-highlighted] {
    background-color: var(--muted);
    color: var(--foreground);
  }

  &[data-selected] {
    background-color: transparent;

    & .indicator {
      opacity: 1;
    }
  }

  &.itemDisabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
}

.itemText {
  flex: 1;
  text-align: left;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.indicator {
  margin-left: 0.5rem;
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  opacity: 0;
  transition: opacity 0.2s ease-in-out;
}

.separator {
  height: 1px;
  background-color: var(--border);
  margin: 0.25rem 0;
}

.selectGroup {
  padding: 0;
}

.groupLabel {
  padding: 0.375rem 0.75rem;
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.backdrop {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 140;
  transition: opacity 150ms;
}
