"use client";

import { Slider } from "@base-ui/react/slider";
import { cn } from "@/lib/utils";
import styles from "./slider.module.css";

function SliderRoot({ className, ...props }: Slider.Root.Props) {
  return <Slider.Root className={cn(styles.root, className)} {...props} />;
}

function SliderControl({ className, ...props }: Slider.Control.Props) {
  return (
    <Slider.Control
      className={cn(styles.control, className)}
      data-slot="slider-control"
      {...props}
    />
  );
}

function SliderTrack({ className, ...props }: Slider.Track.Props) {
  return <Slider.Track className={cn(styles.track, className)} {...props} />;
}

function SliderIndicator({ className, ...props }: Slider.Indicator.Props) {
  return (
    <Slider.Indicator
      className={cn(styles.indicator, className)}
      data-slot="slider-indicator"
      {...props}
    />
  );
}

function SliderThumb({ className, ...props }: Slider.Thumb.Props) {
  return <Slider.Thumb className={cn(styles.thumb, className)} {...props} />;
}

function SliderValue({ className, ...props }: Slider.Value.Props) {
  return <Slider.Value className={cn(styles.value, className)} {...props} />;
}

export {
  SliderRoot as Slider,
  SliderControl,
  SliderIndicator,
  SliderRoot,
  SliderThumb,
  SliderTrack,
  SliderValue,
};

.root {
  position: relative;
  display: flex;
  touch-action: none;
  user-select: none;
  align-items: center;
  width: 100%;
  justify-content: center;

  &[data-orientation="vertical"] {
    flex-direction: column;
    height: 200px;
    width: auto;
  }
}

.control {
  box-sizing: border-box;
  display: flex;
  align-items: center;
  width: 100%;
  padding-block: 0.75rem;
  touch-action: none;
  user-select: none;

  &[data-orientation="vertical"] {
    width: auto;
    height: 100%;
    padding-inline: 0.75rem;
    padding-block: 0;
  }

  @media (max-width: 640px) {
    padding-block: 1rem;
  }
}

.track {
  width: 100%;
  height: 0.25rem;
  background-color: var(--mix-card-75-bg);
  box-shadow: inset 0 0 0 1px var(--mix-card-75-bg);
  border-radius: 0.25rem;
  user-select: none;

  &[data-orientation="vertical"] {
    width: 0.25rem;
    height: 100%;
  }

  @media (max-width: 640px) {
    height: 0.375rem;

    &[data-orientation="vertical"] {
      width: 0.375rem;
    }
  }
}

.indicator {
  border-radius: 0.25rem;
  background-color: var(--primary);
  user-select: none;
  transition: none;
}

.thumb {
  width: 1rem;
  height: 1rem;
  border-radius: 100%;
  background-color: var(--foreground);
  outline: 1px solid var(--border);
  user-select: none;
  transition: none;

  &:has(:focus-visible) {
    outline: 2px solid var(--ring);
  }

  &:disabled {
    pointer-events: none;
    opacity: 0.5;
    cursor: not-allowed;
  }

  @media (max-width: 640px) {
    width: 1.25rem;
    height: 1.25rem;
  }
}

.value {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
  display: block;
  margin-right: 0.5rem;
}
