"use client";

import { Slider } from "@base-ui-components/react/slider";
import { cn } from "@/lib/utils";
import styles from "./slider.module.css";

function SliderRoot({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Root>) {
  return <Slider.Root className={cn(styles.root, className)} {...props} />;
}

function SliderControl({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Control>) {
  return (
    <Slider.Control className={cn(styles.control, className)} {...props} />
  );
}

function SliderTrack({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Track>) {
  return <Slider.Track className={cn(styles.track, className)} {...props} />;
}

function SliderIndicator({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Indicator>) {
  return (
    <Slider.Indicator className={cn(styles.indicator, className)} {...props} />
  );
}

function SliderThumb({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Thumb>) {
  return <Slider.Thumb className={cn(styles.thumb, className)} {...props} />;
}

function SliderValue({
  className,
  ...props
}: React.ComponentProps<typeof Slider.Value>) {
  return <Slider.Value className={cn(styles.value, className)} {...props} />;
}

export {
  SliderControl,
  SliderIndicator,
  SliderRoot,
  SliderThumb,
  SliderTrack,
  SliderValue,
};

.root {
  position: relative;
  display: flex;
  touch-action: none;
  user-select: none;
  align-items: center;
  width: 100%;
  justify-content: center;
}

.root[data-orientation="vertical"] {
  flex-direction: column;
  height: 200px;
  width: auto;
}

.control {
  box-sizing: border-box;
  display: flex;
  align-items: center;
  width: 100%;
  padding-block: 0.75rem;
  touch-action: none;
  user-select: none;
}

.control[data-orientation="vertical"] {
  width: auto;
  height: 100%;
  padding-inline: 0.75rem;
  padding-block: 0;
}

.track {
  width: 100%;
  height: 0.25rem;
  background-color: var(--mix-card-75-bg);
  box-shadow: inset 0 0 0 1px var(--mix-card-75-bg);
  border-radius: 0.25rem;
  user-select: none;
}

.track[data-orientation="vertical"] {
  width: 0.25rem;
  height: 100%;
}

.indicator {
  border-radius: 0.25rem;
  background-color: var(--primary);
  user-select: none;
  transition: none;
}

.thumb {
  width: 1rem;
  height: 1rem;
  border-radius: 100%;
  background-color: var(--foreground);
  outline: 1px solid var(--border);
  user-select: none;
  transition: none;
}

.thumb:has(:focus-visible) {
  outline: 2px solid var(--ring);
}

.thumb:disabled {
  pointer-events: none;
  opacity: 0.5;
  cursor: not-allowed;
}

.value {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
  display: block;
  margin-right: 0.5rem;
}
