files.content:
/* Container for responsive scrolling */
.container {
  position: relative;
  width: 100%;
  overflow-x: auto;
  border-radius: var(--radius);
  -webkit-overflow-scrolling: touch; /* Smooth scrolling on iOS */
}

/* Subtle scrollbar styling */
.container::-webkit-scrollbar {
  height: 8px;
}

.container::-webkit-scrollbar-track {
  background: var(--muted);
  border-radius: 4px;
}

.container::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

.container::-webkit-scrollbar-thumb:hover {
  background: oklch(from var(--border) l c h / 0.8);
}

/* Base table styles */
.table {
  width: 100%;
  caption-side: bottom;
  border-collapse: collapse;
  font-size: 0.925rem;
  line-height: 1.5;
  table-layout: auto;
}

/* Header */
.header {
  position: sticky;
  top: 0;
  background: var(--card);
  z-index: 10;
  border-bottom: 1px solid var(--border);
}

/* Body */
.body {
  background: var(--card);
}

/* Footer */
.footer {
  border-top: 1px solid var(--border);
  background: var(--muted);
  font-weight: 500;
}

/* Row */
.row {
  border-bottom: 1px solid oklch(from var(--border) l c h / 0.5);
  transition: background-color 0.15s var(--ease-out-quad);
}

.body .row:last-child {
  border-bottom: none;
}

.body .row:hover {
  background-color: var(--muted);
}

/* Interactive rows (with onClick handlers) */
.row[onclick]:hover,
.row[role="button"]:hover {
  background-color: var(--accent);
  cursor: pointer;
}

/* Header cell */
.head {
  padding: 0.75rem 1rem;
  text-align: left;
  font-weight: 500;
  color: var(--muted-foreground);
  letter-spacing: -0.01em;
  white-space: nowrap;
  line-height: 1.5;
}

.head[align="center"] {
  text-align: center;
}

.head[align="right"] {
  text-align: right;
}

/* Data cell */
.cell {
  padding: 0.75rem 1rem;
  color: var(--foreground);
  vertical-align: middle;
  line-height: 1.5;
}

.cell[align="center"] {
  text-align: center;
}

.cell[align="right"] {
  text-align: right;
}

/* Caption */
.caption {
  margin-top: 1rem;
  font-size: 0.875rem;
  color: var(--muted-foreground);
  text-align: left;
}

/* Variant: Bordered */
.tableBordered {
  border: 1px solid var(--border);
}

.tableBordered .row {
  border-bottom: 1px solid var(--border);
}

.tableBordered .head,
.tableBordered .cell {
  border-right: 1px solid oklch(from var(--border) l c h / 0.5);
}

.tableBordered .head:last-child,
.tableBordered .cell:last-child {
  border-right: none;
}

/* Variant: Striped */
.tableStriped .body .row:nth-child(odd) {
  background-color: oklch(from var(--muted) l c h / 0.4);
}

.tableStriped .body .row:nth-child(even) {
  background-color: var(--card);
}

.tableStriped .body .row:hover {
  background-color: var(--muted);
}

/* Size: Small */
.tableSm .head,
.tableSm .cell {
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
}

/* Size: Large */
.tableLg .head,
.tableLg .cell {
  padding: 1rem 1.25rem;
  font-size: 1rem;
}

/* Responsive adjustments */
@media (max-width: 640px) {
  .table {
    font-size: 0.875rem;
  }

  .head,
  .cell {
    padding: 0.625rem 0.75rem;
  }

  .tableSm .head,
  .tableSm .cell {
    padding: 0.375rem 0.5rem;
    font-size: 0.8125rem;
  }

  .tableLg .head,
  .tableLg .cell {
    padding: 0.75rem 1rem;
    font-size: 0.925rem;
  }
}

/* Accessibility: Focus states */
.row:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: -2px;
}

/* Improve text readability */
.table {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
