import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import styles from "./table.module.css";

const tableVariants = cva(styles.table, {
  variants: {
    variant: {
      default: "",
      bordered: styles.tableBordered,
      striped: styles.tableStriped,
    },
    size: {
      sm: styles.tableSm,
      md: "",
      lg: styles.tableLg,
    },
  },
  defaultVariants: {
    variant: "default",
    size: "md",
  },
});

function Table({
  className,
  variant,
  size,
  ...props
}: React.ComponentProps<"table"> & VariantProps<typeof tableVariants>) {
  return (
    <div className={styles.container} data-slot="table-container">
      <table className={cn(tableVariants({ variant, size }), className)} data-slot="table" {...props} />
    </div>
  );
}

function TableHeader({ className, ...props }: React.ComponentProps<"thead">) {
  return <thead className={cn(styles.header, className)} data-slot="table-header" {...props} />;
}

function TableBody({ className, ...props }: React.ComponentProps<"tbody">) {
  return <tbody className={cn(styles.body, className)} data-slot="table-body" {...props} />;
}

function TableFooter({ className, ...props }: React.ComponentProps<"tfoot">) {
  return <tfoot className={cn(styles.footer, className)} data-slot="table-footer" {...props} />;
}

function TableRow({ className, ...props }: React.ComponentProps<"tr">) {
  return <tr className={cn(styles.row, className)} data-slot="table-row" {...props} />;
}

function TableHead({ className, ...props }: React.ComponentProps<"th">) {
  return <th className={cn(styles.head, className)} data-slot="table-head" {...props} />;
}

function TableCell({ className, ...props }: React.ComponentProps<"td">) {
  return <td className={cn(styles.cell, className)} data-slot="table-cell" {...props} />;
}

function TableCaption({ className, ...props }: React.ComponentProps<"caption">) {
  return <caption className={cn(styles.caption, className)} data-slot="table-caption" {...props} />;
}

const Root = Table;
const Header = TableHeader;
const Body = TableBody;
const Footer = TableFooter;
const Row = TableRow;
const Head = TableHead;
const Cell = TableCell;
const Caption = TableCaption;

export {
  Root as Table,
  Header as TableHeader,
  Body as TableBody,
  Footer as TableFooter,
  Row as TableRow,
  Head as TableHead,
  Cell as TableCell,
  Caption as TableCaption,
};

.container {
  position: relative;
  width: 100%;
  overflow-x: auto;
  border-radius: var(--radius);
  -webkit-overflow-scrolling: touch;
}

.container::-webkit-scrollbar {
  height: 8px;
}

.container::-webkit-scrollbar-track {
  background: var(--muted);
  border-radius: 4px;
}

.container::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

.container::-webkit-scrollbar-thumb:hover {
  background: oklch(from var(--border) l c h / 0.8);
}

.table {
  width: 100%;
  caption-side: bottom;
  border-collapse: collapse;
  font-size: 0.925rem;
  line-height: 1.5;
  table-layout: auto;
}

.header {
  position: sticky;
  top: 0;
  background: var(--card);
  z-index: 10;
  border-bottom: 1px solid var(--border);
}

.body {
  background: var(--card);
}

.footer {
  border-top: 1px solid var(--border);
  background: var(--muted);
  font-weight: 500;
}

.row {
  border-bottom: 1px solid oklch(from var(--border) l c h / 0.5);
  transition: background-color 0.15s var(--ease-out-quad);
}

.body .row:last-child {
  border-bottom: none;
}

.body .row:hover {
  background-color: var(--muted);
}

.row[onclick]:hover,
.row[role="button"]:hover {
  background-color: var(--accent);
  cursor: pointer;
}

.head {
  padding: 0.75rem 1rem;
  text-align: left;
  font-weight: 500;
  color: var(--muted-foreground);
  letter-spacing: -0.01em;
  white-space: nowrap;
  line-height: 1.5;
}

.head[align="center"] {
  text-align: center;
}

.head[align="right"] {
  text-align: right;
}

.cell {
  padding: 0.75rem 1rem;
  color: var(--foreground);
  vertical-align: middle;
  line-height: 1.5;
}

.cell[align="center"] {
  text-align: center;
}

.cell[align="right"] {
  text-align: right;
}

.caption {
  margin-top: 1rem;
  font-size: 0.875rem;
  color: var(--muted-foreground);
  text-align: left;
}

.tableBordered {
  border: 1px solid var(--border);
}

.tableBordered .row {
  border-bottom: 1px solid var(--border);
}

.tableBordered .head,
.tableBordered .cell {
  border-right: 1px solid oklch(from var(--border) l c h / 0.5);
}

.tableBordered .head:last-child,
.tableBordered .cell:last-child {
  border-right: none;
}

.tableStriped .body .row:nth-child(odd) {
  background-color: oklch(from var(--muted) l c h / 0.4);
}

.tableStriped .body .row:nth-child(even) {
  background-color: var(--card);
}

.tableStriped .body .row:hover {
  background-color: var(--muted);
}

.tableSm .head,
.tableSm .cell {
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
}

.tableLg .head,
.tableLg .cell {
  padding: 1rem 1.25rem;
  font-size: 1rem;
}

@media (max-width: 640px) {
  .table {
    font-size: 0.875rem;
  }

  .head,
  .cell {
    padding: 0.625rem 0.75rem;
  }

  .tableSm .head,
  .tableSm .cell {
    padding: 0.375rem 0.5rem;
    font-size: 0.8125rem;
  }

  .tableLg .head,
  .tableLg .cell {
    padding: 0.75rem 1rem;
    font-size: 0.925rem;
  }
}

.row:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: -2px;
}

.table {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
