import { Tabs } from "@base-ui-components/react/tabs";
import { cn } from "@/lib/utils";
import styles from "./tabs.module.css";

function TabsRoot({
  className,
  ...props
}: React.ComponentProps<typeof Tabs.Root>) {
  return <Tabs.Root className={cn(styles.root, className)} {...props} />;
}

function TabsList({
  className,
  ...props
}: React.ComponentProps<typeof Tabs.List>) {
  return <Tabs.List className={cn(styles.list, className)} {...props} />;
}

function TabsTrigger({
  className,
  ...props
}: React.ComponentProps<typeof Tabs.Tab>) {
  return <Tabs.Tab className={cn(styles.trigger, className)} {...props} />;
}

function TabsContent({
  className,
  ...props
}: React.ComponentProps<typeof Tabs.Panel>) {
  return <Tabs.Panel className={cn(styles.content, className)} {...props} />;
}

function TabsIndicator({
  className,
  ...props
}: React.ComponentProps<typeof Tabs.Indicator>) {
  return (
    <Tabs.Indicator className={cn(styles.indicator, className)} {...props} />
  );
}

export { TabsRoot as Tabs, TabsContent, TabsIndicator, TabsList, TabsTrigger };

.root {
  display: flex;
  flex-direction: column;
  gap: 0;
}

.root.vertical {
  flex-direction: row;
  gap: 1rem;
}

.list {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  border-radius: var(--radius);
  background-color: var(--mix-card-33-bg);
  border-bottom-right-radius: 0;
  border-bottom-left-radius: 0;
  border: 1px solid var(--border);
  padding: 0.25rem;
  gap: 0.25rem;
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.list::-webkit-scrollbar {
  display: none;
}

.vertical .list {
  flex-direction: column;
  align-items: stretch;
  width: 200px;
}

.trigger {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  white-space: nowrap;
  border-radius: var(--radius);
  padding: 0.375rem 0.75rem;
  font-size: 0.875rem;
  font-weight: 450;
  background-color: transparent;
  color: var(--muted-foreground);
  cursor: pointer;
  position: relative;
  margin-right: 0.25rem;
  border: 1px solid transparent;
  flex-shrink: 0;
}

@media (max-width: 640px) {
  .trigger {
    font-size: 0.8125rem;
  }
}

.trigger:hover {
  color: var(--foreground);
}

.trigger:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.trigger:disabled {
  pointer-events: none;
  opacity: 0.5;
}

.trigger[data-selected] {
  background-color: var(--muted);
  color: var(--foreground);
  border: 1px solid var(--border-light, var(--border));
}

.vertical .trigger {
  justify-content: flex-start;
  width: 100%;
}

.content {
  margin-top: 0;
  border-radius: 0 0 var(--radius) var(--radius);
  border: 1px solid var(--border);
  border-top: none;
  padding: 1.5rem;
  background-color: var(--mix-card-33-bg);
}

.content:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.vertical .content {
  margin-top: 0;
  flex: 1;
}

.content[data-state="inactive"] {
  display: none;
}

.content[data-state="active"] {
  display: block;
}

.indicator {
  position: absolute;
  background-color: var(--primary);
  border-radius: var(--radius);
  transition: all 150ms ease-in-out;

  left: var(--active-tab-left);
  top: calc(var(--active-tab-top) + var(--active-tab-height) - 3px);
  width: var(--active-tab-width);
  height: 3px;
}

.content p {
  margin-top: 0;
  margin-bottom: 0;
}
