import { Tabs } from "@base-ui/react/tabs";
import { cn } from "@/lib/utils";
import styles from "./tabs.module.css";

function TabsRoot({ className, ...props }: Tabs.Root.Props) {
  return <Tabs.Root className={cn(className, styles.root)} {...props} data-slot="tabs-root" />;
}

function TabsList({ className, ...props }: Tabs.List.Props) {
  return <Tabs.List className={cn(className, styles.list)} {...props} data-slot="tabs-list" />;
}

function TabsTrigger({ className, ...props }: Tabs.Tab.Props) {
  return <Tabs.Tab className={cn(className, styles.trigger)} {...props} data-slot="tabs-trigger" />;
}

function TabsContent({ className, ...props }: Tabs.Panel.Props) {
  return <Tabs.Panel className={cn(className, styles.content)} {...props} data-slot="tabs-content" />;
}

function TabsIndicator({ className, ...props }: Tabs.Indicator.Props) {
  return <Tabs.Indicator className={cn(className, styles.indicator)} data-slot="tabs-indicator" {...props} />;
}

export { TabsRoot as Tabs, TabsContent, TabsIndicator, TabsList, TabsTrigger };

.root {
  display: flex;
  flex-direction: column;
  gap: 0;

  &[data-orientation="vertical"] {
    flex-direction: row;
    gap: 1rem;
  }
}

.list {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  border-radius: var(--radius);
  background-color: var(--mix-card-33-bg);
  border-bottom-right-radius: 0;
  border-bottom-left-radius: 0;
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  padding: 0.25rem;
  gap: 0.25rem;
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    display: none;
  }
}

.root[data-orientation="vertical"] .list {
  flex-direction: column;
  align-items: stretch;
  width: 200px;
}

.trigger {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  white-space: nowrap;
  border-radius: var(--radius);
  padding: 0.375rem 0.75rem;
  font-size: 0.875rem;
  font-weight: 450;
  line-height: 1.375;
  background-color: transparent;
  color: var(--muted-foreground);
  cursor: pointer;
  position: relative;
  margin-right: 0.25rem;
  border: 1px solid transparent;
  flex-shrink: 0;

  &:hover {
    color: var(--foreground);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
  }

  &:disabled {
    pointer-events: none;
    opacity: 0.5;
  }

  &[data-active] {
    background-color: var(--muted);
    color: var(--foreground);
    border: 1px solid var(--border);
  }

  @media (max-width: 640px) {
    font-size: 0.8125rem;
  }
}

.root[data-orientation="vertical"] .trigger {
  justify-content: flex-start;
  width: 100%;
}

.content {
  margin-top: 0;
  border-radius: 0 0 var(--radius) var(--radius);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  border-top: none;
  padding: 1.5rem;
  background-color: var(--mix-card-33-bg);

  &:focus-visible {
    outline: 2px solid var(--ring);
  }

  &[data-state="inactive"] {
    display: none;
  }

  &[data-state="active"] {
    display: block;
  }

  & p {
    margin-top: 0;
    margin-bottom: 0;
  }
}

.root[data-orientation="vertical"] .content {
  margin-top: 0;
  flex: 1;
}

.indicator {
  position: absolute;
  background-color: var(--primary);
  border-radius: var(--radius);
  transition: all 150ms ease-in-out;

  left: var(--active-tab-left);
  top: calc(var(--active-tab-top) + var(--active-tab-height) - 3px);
  width: var(--active-tab-width);
  height: 3px;
}
