"use client";

import { Toast } from "@base-ui/react/toast";
import { X } from "lucide-react";
import type * as React from "react";
import { cn } from "@/lib/utils";
import styles from "./toast.module.css";

const toastManager = Toast.createToastManager();
const anchoredToastManager = Toast.createToastManager();

function ToastProvider({ children, ...props }: Toast.Provider.Props) {
  return (
    <Toast.Provider toastManager={toastManager} {...props}>
      {children}
      <ToastPortal>
        <ToastViewport>
          <ToastList />
        </ToastViewport>
      </ToastPortal>
    </Toast.Provider>
  );
}

function ToastPortal({ ...props }: Toast.Portal.Props) {
  return <Toast.Portal {...props} />;
}

function ToastViewport({ className, ...props }: Toast.Viewport.Props) {
  return <Toast.Viewport className={cn(styles.viewport, className)} data-slot="toast-viewport" {...props} />;
}

function ToastRoot({ className, ...props }: Toast.Root.Props) {
  return <Toast.Root className={cn(styles.root, className)} {...props} />;
}

function ToastContent({ className, ...props }: Toast.Content.Props) {
  return <Toast.Content className={cn(styles.content, className)} {...props} />;
}

function ToastTitle({ className, ...props }: Toast.Title.Props) {
  return <Toast.Title className={cn(styles.title, className)} {...props} />;
}

function ToastDescription({ className, ...props }: Toast.Description.Props) {
  return <Toast.Description className={cn(styles.description, className)} data-slot="toast-description" {...props} />;
}

function ToastAction({ className, ...props }: Toast.Action.Props) {
  return <Toast.Action className={cn(styles.action, className)} data-slot="toast-action" {...props} />;
}

function ToastClose({
  className,
  render,
  ...props
}: Toast.Close.Props & {
  render?: (props: React.ButtonHTMLAttributes<HTMLButtonElement>) => React.ReactNode;
}) {
  return <Toast.Close className={cn(styles.close, className)} data-slot="toast-close" render={render} {...props} />;
}

function ToastList() {
  const { toasts } = Toast.useToastManager();
  return toasts.map((toast) => (
    <ToastRoot key={toast.id} toast={toast}>
      <ToastContent>
        <div
          style={{
            display: "flex",
            alignItems: "start",
            justifyContent: "space-between",
            gap: "0.5rem",
          }}
        >
          <div style={{ flex: 1, minWidth: 0 }}>
            <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
              <ToastTitle />
              {toast.type && <span className={styles.statusPill} data-type={toast.type} />}
            </div>
            <ToastDescription />
            {toast.actionProps && (
              <ToastAction className={styles.actionButton}>{toast.actionProps.children}</ToastAction>
            )}
          </div>
        </div>
        {toast.data?.showCloseButton === true && (
          <ToastClose
            render={(props: React.ButtonHTMLAttributes<HTMLButtonElement>) => (
              <button {...props} aria-label="Close">
                <X className="h-4 w-4" />
              </button>
            )}
          />
        )}
      </ToastContent>
    </ToastRoot>
  ));
}

function AnchoredToasts() {
  const { toasts } = Toast.useToastManager();

  return (
    <Toast.Portal>
      <Toast.Viewport className={styles.anchoredViewport}>
        {toasts.map((toast) => {
          const positionerProps = toast.positionerProps;

          if (!positionerProps?.anchor) {
            return null;
          }

          return (
            <Toast.Positioner
              className={styles.anchoredPositioner}
              key={toast.id}
              sideOffset={positionerProps.sideOffset ?? 4}
              toast={toast}
            >
              <Toast.Root className={styles.anchoredRoot} toast={toast}>
                <Toast.Content className={styles.anchoredContent}>
                  <Toast.Title className={styles.anchoredTitle} />
                </Toast.Content>
              </Toast.Root>
            </Toast.Positioner>
          );
        })}
      </Toast.Viewport>
    </Toast.Portal>
  );
}

function AnchoredToastProvider({ children }: { children: React.ReactNode }) {
  return (
    <Toast.Provider toastManager={anchoredToastManager}>
      {children}
      <AnchoredToasts />
    </Toast.Provider>
  );
}

export { ToastProvider, toastManager, AnchoredToastProvider, anchoredToastManager };

.viewport {
  position: fixed;
  width: 250px;
  margin: 0 auto;
  bottom: 1rem;
  right: 1rem;
  left: auto;
  top: auto;
  z-index: 1000;

  @media (min-width: 500px) {
    bottom: 2rem;
    right: 2rem;
    width: 340px;
  }
}

.root {
  --gap: 0.75rem;
  --peek: 0.75rem;
  --scale: calc(max(0, 1 - (var(--toast-index) * 0.1)));
  --shrink: calc(1 - var(--scale));
  --height: var(--toast-frontmost-height, var(--toast-height));
  --offset-y: calc(
    var(--toast-offset-y) *
    -1 +
    (var(--toast-index) * var(--gap) * -1) +
    var(--toast-swipe-movement-y)
  );
  position: absolute;
  right: 0;
  margin: 0 auto;
  box-sizing: border-box;

  background: var(--mix-card-5-bg);
  color: var(--muted-foreground);
  border: 0.5px solid oklch(from var(--border) l c h / 0.5);
  padding: 1rem;
  width: 100%;
  box-shadow:
    oklch(from var(--border) l c h / 0.2) 0px 1px 1px,
    oklch(from var(--border) l c h / 0.2) 0px 1px 1px,
    oklch(from var(--border) l c h / 0.2) 0px 1px 1px;
  background-clip: padding-box;
  border-radius: 0.5rem;
  transform-origin: bottom center;
  bottom: 0;
  left: auto;
  margin-right: 0;
  -webkit-user-select: none;
  user-select: none;
  transition:
    transform 0.35s ease,
    opacity 0.5s,
    height 0.3s var(--ease-out-expo);
  cursor: default;
  z-index: calc(1000 - var(--toast-index));
  height: var(--height);
  transform: translateX(var(--toast-swipe-movement-x))
    translateY(
      calc(
        var(--toast-swipe-movement-y) -
        (var(--toast-index) * var(--peek)) -
        (var(--shrink) * var(--height))
      )
    )
    scale(var(--scale));

  &[data-expanded] {
    transform: translateX(var(--toast-swipe-movement-x))
      translateY(var(--offset-y));
    height: var(--toast-height);
  }

  &[data-starting-style] {
    transform: translateY(150%);
    opacity: 0;
  }

  &[data-ending-style] {
    transform: translateY(150%);
  }

  &[data-limited] {
    opacity: 0;
  }

  &[data-ending-style] {
    opacity: 0;

    &[data-swipe-direction="up"] {
      transform: translateY(calc(var(--toast-swipe-movement-y) - 150%));
    }
    &[data-swipe-direction="left"] {
      transform: translateX(calc(var(--toast-swipe-movement-x) - 150%))
        translateY(var(--offset-y));
    }
    &[data-swipe-direction="right"] {
      transform: translateX(calc(var(--toast-swipe-movement-x) + 150%))
        translateY(var(--offset-y));
    }
    &[data-swipe-direction="down"] {
      transform: translateY(calc(var(--toast-swipe-movement-y) + 150%));
    }
  }

  &::after {
    content: "";
    position: absolute;
    top: 100%;
    width: 100%;
    left: 0;
    height: calc(var(--gap) + 1px);
  }
}

.content {
  overflow: hidden;
  transition: opacity 0.5s;

  &[data-behind] {
    pointer-events: none;
    opacity: 0;
  }

  &[data-expanded] {
    pointer-events: auto;
    opacity: 1;
  }
}

.title {
  font-weight: 450;
  font-size: 0.975rem;
  line-height: 1.25rem;
  letter-spacing: -0.02em;
  margin: 0;
  color: var(--foreground);
}

.description {
  font-size: 0.875rem;
  line-height: 1.25rem;
  letter-spacing: -0.02em;
  margin: 0;
  margin-top: 0.25rem;
  color: var(--secondary-foreground);
}

.statusPill {
  display: inline-block;
  width: 10px;
  height: 5px;
  border-radius: var(--radius);
}

.statusPill[data-type="success"] {
  background-color: var(--success);
}

.statusPill[data-type="error"] {
  background-color: var(--destructive);
}

.statusPill[data-type="warning"] {
  background-color: var(--warning);
}

.statusPill[data-type="info"] {
  background-color: var(--info);
}

.action {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 2rem;
  padding: 0 0.75rem;
  font-size: 0.875rem;
  font-weight: 500;
  line-height: 1.25rem;
  border-radius: 0.25rem;
  margin-top: 0.5rem;
  background-color: var(--primary);
  color: var(--primary-foreground);
  border: none;
  cursor: pointer;
  transition: background-color 0.15s ease;

  &:hover {
    background-color: var(--primary);
    opacity: 0.9;
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }
}

.actionButton {
  margin-top: 0.5rem;
}

.close {
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  border: none;
  background: transparent;
  width: 1.25rem;
  height: 1.25rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 0.25rem;
  color: oklch(from var(--foreground) l c h / 0.3);
  cursor: pointer;
  transition: all 0.15s ease;

  &:hover {
    background-color: var(--muted);
    color: var(--foreground);
  }

  &:focus {
    outline: none;
  }

  &:focus-visible {
    outline: 2px solid var(--ring, var(--color-blue));
    outline-offset: 2px;
  }

  svg {
    height: 1rem;
    width: 1rem;
  }
}

/* Anchored Toast Styles */
.anchoredViewport {
  outline: 0;
}

.anchoredPositioner {
  z-index: 50;
  max-width: min(16rem, var(--available-width));
}

.anchoredRoot {
  position: relative;
  text-wrap: balance;
  border: 0.5px solid var(--border);
  background: var(--popover);
  background-clip: padding-box;
  color: var(--popover-foreground);
  font-size: 0.75rem;
  border-radius: calc(var(--radius) - 2px);
  box-shadow: 0 4px 6px -1px oklch(from var(--foreground) l c h / 0.05);
  transition:
    scale 0.15s,
    opacity 0.15s;

  &::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0;
    border-radius: calc(var(--radius) - 3px);
    box-shadow: inset 0 1px 0 oklch(from var(--background) l c h / 0.04);
  }

  &[data-starting-style] {
    scale: 0.98;
    opacity: 0;
  }

  &[data-ending-style] {
    scale: 0.98;
    opacity: 0;
  }
}

.anchoredContent {
  pointer-events: auto;
  padding: 0.25rem 0.5rem;
}

.anchoredTitle {
  font-weight: 400;
  margin: 0;
  white-space: nowrap;
}
