"use client";

import { Toggle } from "@base-ui-components/react/toggle";
import { cn } from "@/lib/utils";
import styles from "./toggle.module.css";

function ToggleRoot({ className, ...props }: Toggle.Props) {
  return <Toggle className={cn(styles.root, className)} {...props} />;
}

export { ToggleRoot as Toggle };

.root {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: var(--radius);
  font-size: 0.875rem;
  font-weight: 500;
  height: 2.25rem;
  padding: 0 0.75rem;
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  background-color: var(--mix-card-33-bg);
  color: var(--foreground);
  cursor: pointer;
  transition: all 150ms ease-out;

  &:hover {
    background-color: var(--muted);
  }

  &[data-focused] {
    outline: none;
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
  }

  &[data-pressed] {
    background-color: var(--mix-card-66-bg);
    color: var(--foreground);
    border: 0.5px solid oklch(from var(--border) l c h / 0.9);

    &:hover {
      background-color: var(--mix-card-85-bg);
    }
  }

  &[data-disabled] {
    pointer-events: none;
    opacity: 0.5;
  }
}
