"use client";

import { Toolbar } from "@base-ui-components/react/toolbar";
import { cn } from "@/lib/utils";
import styles from "./toolbar.module.css";

function ToolbarRoot({
  className,
  orientation = "horizontal",
  ...props
}: Toolbar.Root.Props) {
  return (
    <Toolbar.Root
      className={cn(styles.root, className)}
      data-orientation={orientation}
      data-slot="toolbar-root"
      orientation={orientation}
      {...props}
    />
  );
}

function ToolbarButton({ className, ...props }: Toolbar.Button.Props) {
  return <Toolbar.Button className={cn(styles.button, className)} {...props} />;
}

function ToolbarLink({ className, ...props }: Toolbar.Link.Props) {
  return <Toolbar.Link className={cn(styles.link, className)} {...props} />;
}

function ToolbarInput({ className, ...props }: Toolbar.Input.Props) {
  return <Toolbar.Input className={cn(styles.input, className)} {...props} />;
}

function ToolbarGroup({ className, ...props }: Toolbar.Group.Props) {
  return <Toolbar.Group className={cn(styles.group, className)} {...props} />;
}

function ToolbarSeparator({ className, ...props }: Toolbar.Separator.Props) {
  return (
    <Toolbar.Separator
      className={cn(styles.separator, className)}
      data-slot="toolbar-separator"
      {...props}
    />
  );
}

export {
  ToolbarRoot as Toolbar,
  ToolbarButton,
  ToolbarGroup,
  ToolbarInput,
  ToolbarLink,
  ToolbarSeparator,
};

.root {
  display: flex;
  align-items: center;
  gap: 0.25rem;
  border-radius: var(--radius);
  background-color: var(--mix-card-33-bg);
  border: 0.5px solid oklch(from var(--border) l c h / 0.9);

  &[data-orientation="vertical"] {
    flex-direction: column;
    align-items: stretch;
  }

  @media (max-width: 640px) {
    gap: 0.375rem;
    padding: 0.5rem;
  }
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.375rem 0.75rem;
  border-radius: calc(var(--radius) - 2px);
  border: none;
  background-color: transparent;
  color: var(--foreground);
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  min-height: 2rem;
  min-width: 2rem;

  &:hover {
    background-color: var(--accent);
    color: var(--accent-foreground);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  &[data-state="pressed"] {
    background-color: var(--accent);
    color: var(--accent-foreground);
  }

  @media (max-width: 640px) {
    padding: 0.5rem 1rem;
    font-size: 0.9375rem;
    min-height: 2.5rem;
    min-width: 2.5rem;
  }
}

.link {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.375rem 0.75rem;
  border-radius: calc(var(--radius) - 2px);
  color: var(--foreground);
  text-decoration: none;
  font-size: 0.875rem;
  font-weight: 500;
  transition: all 0.2s;
  min-height: 2rem;
  min-width: 2rem;

  &:hover {
    background-color: var(--accent);
    color: var(--accent-foreground);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  @media (max-width: 640px) {
    padding: 0.5rem 1rem;
    font-size: 0.9375rem;
    min-height: 2.5rem;
    min-width: 2.5rem;
  }
}

.input {
  flex: 1;
  padding: 0.375rem 0.75rem;
  border-radius: calc(var(--radius) - 2px);
  border: 1px solid var(--border);
  background-color: var(--input);
  color: var(--foreground);
  font-size: 0.875rem;
  min-height: 2rem;
  transition: all 0.2s;

  &:focus {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
    border-color: transparent;
  }

  &::placeholder {
    color: var(--muted-foreground);
  }

  @media (max-width: 640px) {
    padding: 0.5rem 1rem;
    font-size: 0.9375rem;
    min-height: 2.5rem;
  }
}

.group {
  display: flex;
  align-items: center;
  gap: 0.125rem;
  padding: 0.125rem;
  border-radius: calc(var(--radius) - 2px);
  background-color: var(--muted);

  .root[data-orientation="vertical"] & {
    flex-direction: column;
    align-items: stretch;
  }

  @media (max-width: 640px) {
    gap: 0.1875rem;
    padding: 0.1875rem;
  }
}

.separator {
  width: 1px;
  height: 1.5rem;
  background-color: var(--border);
  flex-shrink: 0;
  margin: 0 0.25rem;

  .root[data-orientation="vertical"] & {
    width: 100%;
    height: 1px;
    margin: 0.25rem 0;
  }

  @media (max-width: 640px) {
    height: 1.75rem;
  }
}
