"use client";

import { cn } from "@/lib/utils";
import { Tooltip } from "@base-ui-components/react/tooltip";
import styles from "./tooltip.module.css";

function TooltipProvider({
  ...props
}: React.ComponentProps<typeof Tooltip.Provider>) {
  return <Tooltip.Provider {...props} />;
}

function TooltipRoot({ ...props }: React.ComponentProps<typeof Tooltip.Root>) {
  return <Tooltip.Root {...props} />;
}

function TooltipTrigger({
  ...props
}: React.ComponentProps<typeof Tooltip.Trigger>) {
  return <Tooltip.Trigger {...props} />;
}

const TooltipPortal = Tooltip.Portal;

function TooltipPositioner({
  className,
  ...props
}: React.ComponentProps<typeof Tooltip.Positioner>) {
  return (
    <Tooltip.Positioner
      className={cn(styles.positioner, className)}
      {...props}
    />
  );
}

function TooltipPopup({
  className,
  ...props
}: React.ComponentProps<typeof Tooltip.Popup>) {
  return <Tooltip.Popup className={cn(styles.popup, className)} {...props} />;
}

function TooltipArrow({
  className,
  ...props
}: React.ComponentProps<typeof Tooltip.Arrow>) {
  return (
    <Tooltip.Arrow className={cn(styles.arrow, className)} {...props}>
      <ArrowSvg />
    </Tooltip.Arrow>
  );
}

function ArrowSvg(props: React.ComponentProps<"svg">) {
  return (
    <svg fill="none" height="10" viewBox="0 0 20 10" width="20" {...props}>
      <path
        className={styles.arrowFill}
        d="M9.66437 2.60207L4.80758 6.97318C4.07308 7.63423 3.11989 8 2.13172 8H0V10H20V8H18.5349C17.5468 8 16.5936 7.63423 15.8591 6.97318L11.0023 2.60207C10.622 2.2598 10.0447 2.25979 9.66437 2.60207Z"
      />
      <path
        className={styles.arrowOuterStroke}
        d="M8.99542 1.85876C9.75604 1.17425 10.9106 1.17422 11.6713 1.85878L16.5281 6.22989C17.0789 6.72568 17.7938 7.00001 18.5349 7.00001L15.89 7L11.0023 2.60207C10.622 2.2598 10.0447 2.2598 9.66436 2.60207L4.77734 7L2.13171 7.00001C2.87284 7.00001 3.58774 6.72568 4.13861 6.22989L8.99542 1.85876Z"
      />
      <path
        className={styles.arrowInnerStroke}
        d="M10.3333 3.34539L5.47654 7.71648C4.55842 8.54279 3.36693 9 2.13172 9H0V8H2.13172C3.11989 8 4.07308 7.63423 4.80758 6.97318L9.66437 2.60207C10.0447 2.25979 10.622 2.2598 11.0023 2.60207L15.8591 6.97318C16.5936 7.63423 17.5468 8 18.5349 8H20V9H18.5349C17.2998 9 16.1083 8.54278 15.1901 7.71648L10.3333 3.34539Z"
      />
    </svg>
  );
}

export {
  ArrowSvg,
  TooltipRoot as Tooltip,
  TooltipArrow,
  TooltipPopup,
  TooltipPortal,
  TooltipPositioner,
  TooltipProvider,
  TooltipTrigger,
};

.positioner {
  z-index: 9999;
}

.popup {
  max-width: 20rem;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  color: var(--primary);
  background-color: var(--mix-card-33-bg);
  border-radius: var(--radius);
  box-shadow:
    0 10px 15px -3px rgba(0, 0, 0, 0.1),
    0 4px 6px -2px rgba(0, 0, 0, 0.05);
  transition:
    transform 0.125s ease-out,
    opacity 0.125s ease-out;
  transform-origin: var(--transform-origin);
  word-wrap: break-word;
  position: relative;
  margin-bottom: 0.25rem;
  border: 1px solid var(--border);
}

.popup[data-starting-style],
.popup[data-ending-style] {
  opacity: 0;
  transform: scale(0.97);
}

.popup[data-instant] {
  transition-duration: 0ms;
}

.arrow {
  position: absolute;
  width: 20px;
  height: 10px;
  z-index: 1;

  &[data-side="top"] {
    bottom: -8px;
    transform: rotate(180deg);
  }

  &[data-side="bottom"] {
    top: -8px;
    transform: rotate(0deg);
  }

  &[data-side="left"] {
    right: 8px;
    transform: rotate(90deg);
  }

  &[data-side="right"] {
    left: -8x;
    transform: rotate(-90deg);
  }

  svg {
    width: 100%;
    height: 100%;
    display: block;
  }
}

.arrowFill {
  fill: var(--mix-card-33-bg);
}

.arrowOuterStroke {
  fill: var(--border);
}

.arrowInnerStroke {
  fill: rgba(0, 0, 0, 0.05);
}
